// Script uses dates generated by dateStyle.js
// Autumn: September 1st - November 30th

var profileImage = document.getElementById("profileImage");
switch (year)
{
    default:
        switch (month)
        {
            default:
                break;
            case 1: // February
                switch (day)
                {
                    case 14:
                        profileImage.src = "Img/ValentineProfile.png"
                        break;
                }
                break;
            case 8: // September
                profileImage.src = "Img/AutumnProfile.png";
                break;
            case 9: // October
                switch (day)
                {
                    default: // Autumn
                        profileImage.src = "Img/AutumnProfile.png";
                        break;
                    case 1: // Birthday
                        break;
                    case 31: // Halloween
                        profileImage.src = "Img/HalloweenProfile.png";
                }
            case 10: // November
                profileImage.src = "Img/AutumnProfile.png";
                break;
            case 11: // December
                if (day < 25) // Runup to Christmas
                {
                    profileImage.src = "Img/ChristmasLeadupProfile.png";
                }
                else if (day == 25) // Christmas day
                {
                    profileImage.src = "Img/ChristmasProfile.png";
                }
                else if (day == 26) // Boxing day
                {
                    profileImage.src = "Img/ChristmasOverProfile.png";
                }
                else if (day == 31) // New year's eve
                {
                    profileImage.src = "Img/NewYearEveProfile.png";
                }
                break;
        }
        break;
}